import React from "react";

function TutorButton(callTutor) {

    const onHandleTutor = () => {
        const alertString = 'When playing with a two-stone handicap in Go, prioritize making strong and efficient groups while aiming for a balanced board position. Early on, focus on building influence and securing territory. Additionally, keep an eye on key points and potential weaknesses in your opponent\'s position to take advantage of any opportunities that arise.'
        window.alert(alertString);
    };

    return (
        <button className="tutor-button" onClick={onHandleTutor}>
            tutor
        </button>
    )

}

export default TutorButton;
